    fetch('http://localhost:8000/users')
    .then((res)=> res.json())
    .then((data) => setUsers(data))
  },[])
  return (
    <div className='App'>
      <header className='App-header'>
        <form onSubmit={onSubmit}>
          <label>
            Name:
            <input onChange={onChange} name="name" value={name}></input>
          </label>
    <div>
