  }

  const onSubmit=(e)=>{
    e.preventDefault();
  }


  useEffect(()=>{
    fetch('http://localhost:8000/users')
    .then((res)=> res.json())
    .then((data) => setUsers(data))
  },[])
  return (
    <div className='App'>
      <header className='App-header'>
        <form onSubmit={onSubmit}>
          <label>
            Name:
            <input onChange={onChange} name="name" value={name}></input>
          </label>
          <hr/>

          <label>
            ID Number:
            <input onChangeEmail={(e)=>setIdNumber(e.target.value)} name="ID" value={userID}></input>
          </label>
    <div>
    <button onClick={showData}>Submit</button>
    </div>
        </form>

      </header>
      <h1></h1>

     {users.map((user)=>(<>
     <h2>{user?.name}</h2>
     <h2>{user.email}</h2>
     </>))}


 </div>
  );
}



export default App;
